import React, { useEffect, useState } from "react";

const MovieRecommendation = ({ recommendations = [], currentIndex = 0 }) => {
  const movie = recommendations[currentIndex];

  if (!movie) return null;

  return (
    <section className="recommendation-section">
      <div className="recommendation-content">
        <img
          src={movie["#IMG_POSTER"] || "https://via.placeholder.com/200x300"}
          alt={movie["#TITLE"]}
          className="recommendation-image"
        />
        <div className="recommendation-details">
          <h2>{movie["#TITLE"]}</h2>
          <p><strong>Year:</strong> {movie["#YEAR"] || "N/A"}</p>
          <p><strong>Duration:</strong> {movie["#DURATION"] || "N/A"}</p>
          <p><strong>Episodes:</strong> {movie["#EPISODES"] || "N/A"}</p>
          <p>{movie["#DESCRIPTION"] || movie["description"] || "No description available."}</p>

          <div className="recommendation-buttons">
            <button className="btn" onClick={() => alert("Added to Favorites!")}>Favorite</button>
            <button className="btn" onClick={() => alert("Playing Trailer...")}>Watch Trailer</button>
            <button className="btn" onClick={() => alert("Playing Movie...")}>Play</button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default MovieRecommendation;
