import React from "react";

const movieData = {
  Trending: [
    {
      "#TITLE": "Trending Movie 1",
      "#IMG_POSTER": "https://via.placeholder.com/160x240?text=Trending+1",
      "#YEAR": "2024",
    },
    {
      "#TITLE": "Trending Movie 2",
      "#IMG_POSTER": "https://via.placeholder.com/160x240?text=Trending+2",
      "#YEAR": "2023",
    },
  ],
  Marvel: [
    {
      "#TITLE": "Marvel Movie 1",
      "#IMG_POSTER": "https://via.placeholder.com/160x240?text=Marvel+1",
      "#YEAR": "2022",
    },
    {
      "#TITLE": "Marvel Movie 2",
      "#IMG_POSTER": "https://via.placeholder.com/160x240?text=Marvel+2",
      "#YEAR": "2023",
    },
  ],
  "Harry Potter": [
    {
      "#TITLE": "Harry Potter 1",
      "#IMG_POSTER": "https://via.placeholder.com/160x240?text=HP+1",
      "#YEAR": "2001",
    },
    {
      "#TITLE": "Harry Potter 2",
      "#IMG_POSTER": "https://via.placeholder.com/160x240?text=HP+2",
      "#YEAR": "2002",
    },
  ],
  "Star Wars": [
    {
      "#TITLE": "Star Wars 1",
      "#IMG_POSTER": "https://via.placeholder.com/160x240?text=Star+Wars+1",
      "#YEAR": "1977",
    },
    {
      "#TITLE": "Star Wars 2",
      "#IMG_POSTER": "https://via.placeholder.com/160x240?text=Star+Wars+2",
      "#YEAR": "1980",
    },
  ],
  "Top Rated": [
    {
      "#TITLE": "Top Rated 1",
      "#IMG_POSTER": "https://via.placeholder.com/160x240?text=Top+Rated+1",
      "#YEAR": "2020",
    },
    {
      "#TITLE": "Top Rated 2",
      "#IMG_POSTER": "https://via.placeholder.com/160x240?text=Top+Rated+2",
      "#YEAR": "2019",
    },
  ],
};

const Home = () => {
  return (
    <div>
      {Object.keys(movieData).map((category) => {
        const id = category.toLowerCase().replace(/\s+/g, "-");
        return (
          <section
            key={category}
            id={id}
            style={{ padding: "20px", marginBottom: "40px" }}
          >
            <h2 style={{ color: "white" }}>{category}</h2>
            <div
              style={{
                display: "flex",
                gap: "15px",
                overflowX: "auto",
                paddingBottom: "10px",
              }}
            >
              {movieData[category].map((movie, idx) => (
                <div
                  key={idx}
                  style={{
                    minWidth: "160px",
                    color: "white",
                    cursor: "pointer",
                    textAlign: "center",
                  }}
                >
                  <img
                    src={movie["#IMG_POSTER"]}
                    alt={movie["#TITLE"]}
                    style={{ width: "160px", height: "240px", borderRadius: 6 }}
                  />
                  <p style={{ fontWeight: "bold", margin: "10px 0 5px" }}>
                    {movie["#TITLE"]}
                  </p>
                  <p style={{ fontSize: "0.9rem", color: "#bbb" }}>
                    {movie["#YEAR"]}
                  </p>
                </div>
              ))}
            </div>
          </section>
        );
      })}
    </div>
  );
};

export default Home;
