import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Predefined colors to assign to profiles
const colors = ['#e50914', '#1f80e0', '#f5a623', '#50c878', '#9b59b6', '#3498db', '#e67e22'];

const initialProfiles = [
  { id: 1, name: 'Person 1', avatar: 'ðŸ‘¤' },
  { id: 2, name: 'Person 2', avatar: 'ðŸ‘©' },
  { id: 3, name: 'Kids', avatar: 'ðŸ§’' },
];

const Profiles = () => {
  const navigate = useNavigate();

  const [profiles, setProfiles] = useState(initialProfiles);
  const [newProfileName, setNewProfileName] = useState('');

  // Select a profile and save it in localStorage, then navigate
  const selectProfile = (profile) => {
    localStorage.setItem('profile', JSON.stringify(profile));
    navigate('/home');
  };

  // Add new profile to state with a unique id and avatar
  const addProfile = () => {
    if (!newProfileName.trim()) return; // prevent empty names

    const newProfile = {
      id: profiles.length ? profiles[profiles.length - 1].id + 1 : 1,
      name: newProfileName,
      avatar: 'ðŸ™‚', // default avatar for new profiles
    };

    setProfiles([...profiles, newProfile]);
    setNewProfileName('');
  };

  return (
    <div style={styles.container}>
      <h2 style={{ color: 'white', marginBottom: 50 }}>Who is watching?</h2>

      <div style={styles.profilesGrid}>
        {profiles.map((profile, index) => (
          <div
            key={profile.id}
            style={{
              ...styles.profileCard,
              backgroundColor: colors[index % colors.length], // assign color based on index
            }}
            onClick={() => selectProfile(profile)}
            onMouseEnter={(e) => (e.currentTarget.style.transform = 'scale(1.1)')}
            onMouseLeave={(e) => (e.currentTarget.style.transform = 'scale(1)')}
          >
            <div style={styles.avatar}>{profile.avatar}</div>
            <div style={styles.name}>{profile.name}</div>
          </div>
        ))}

        {/* Add Profile Card */}
        <div style={{ ...styles.profileCard, backgroundColor: '#555' }}>
          <input
            type="text"
            placeholder="New profile name"
            value={newProfileName}
            onChange={(e) => setNewProfileName(e.target.value)}
            style={styles.input}
            onKeyDown={(e) => {
              if (e.key === 'Enter') addProfile();
            }}
          />
          <button onClick={addProfile} style={styles.addButton}>
            Add
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    backgroundColor: '#141414',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
  },
  profilesGrid: {
    display: 'flex',
    gap: '40px',
    flexWrap: 'wrap',
    justifyContent: 'center',
    alignItems: 'center',
  },
  profileCard: {
    cursor: 'pointer',
    width: 160,
    height: 160,
    borderRadius: 8,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
    fontSize: 20,
    transition: 'transform 0.2s ease',
    boxShadow: '0 4px 15px rgba(255, 255, 255, 0.1)',
    userSelect: 'none',
    position: 'relative',
    padding: 10,
  },
  avatar: {
    fontSize: 60,
    marginBottom: 10,
  },
  name: {
    fontWeight: 'bold',
    textAlign: 'center',
  },
  input: {
    width: '100%',
    padding: '8px 10px',
    borderRadius: 4,
    border: 'none',
    fontSize: 16,
    marginBottom: 10,
    outline: 'none',
  },
  addButton: {
    width: '100%',
    padding: '8px 0',
    backgroundColor: '#e50914',
    border: 'none',
    borderRadius: 5,
    color: 'white',
    fontWeight: 'bold',
    cursor: 'pointer',
    fontSize: 16,
  },
};

export default Profiles;
