import React from 'react';
import { useNavigate } from 'react-router-dom';

const profiles = [
  { id: 1, name: 'Person 1', avatar: 'ðŸ‘¤' },
  { id: 2, name: 'Person 2', avatar: 'ðŸ‘©' },
  { id: 3, name: 'Kids', avatar: 'ðŸ§’' },
];

const Profiles = () => {
  const navigate = useNavigate();

  const selectProfile = (profile) => {
    localStorage.setItem('profile', JSON.stringify(profile));
    navigate('/home');
  };

  return (
    <div style={styles.container}>
      <h2 style={{ color: 'white', marginBottom: 50 }}>Who is watching?</h2>
      <div style={styles.profilesGrid}>
        {profiles.map((profile) => (
          <div
            key={profile.id}
            style={styles.profileCard}
            onClick={() => selectProfile(profile)}
            onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.1)'}
            onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}
          >
            <div style={styles.avatar}>{profile.avatar}</div>
            <div style={styles.name}>{profile.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    backgroundColor: '#141414',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
  },
  profilesGrid: {
    display: 'flex',
    gap: '40px',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  profileCard: {
    cursor: 'pointer',
    width: 160,
    height: 160,
    backgroundColor: '#333',
    borderRadius: 8,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
    fontSize: 20,
    transition: 'transform 0.2s ease',
    boxShadow: '0 4px 15px rgba(255, 255, 255, 0.1)',
    userSelect: 'none',
  },
  avatar: {
    fontSize: 60,
    marginBottom: 10,
  },
  name: {
    fontWeight: 'bold',
  },
};

export default Profiles;
