import React, { useEffect, useState } from 'react';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import CloseIcon from '@mui/icons-material/Close';
import '../styles/MovieRecommendation.css';

const recommendations = [
  {
    title: 'Breaking Bad',
    duration: '5 Seasons',
    episodes: '62 Episodes',
    description:
      'A chemistry teacher diagnosed with cancer turns to a life of crime to secure his family’s future.',
    trailerUrl: 'https://www.w3schools.com/html/mov_bbb.mp4',
  },
  {
    title: 'Stranger Things',
    duration: '4 Seasons',
    episodes: '34 Episodes',
    description:
      'A group of young friends witness supernatural forces and secret government exploits.',
    trailerUrl: 'https://www.w3schools.com/html/movie.mp4',
  },
  {
    title: 'The Crown',
    duration: '6 Seasons',
    episodes: '60 Episodes',
    description:
      'A dramatic portrayal of the reign of Queen Elizabeth II and the events that shaped the second half of the 20th century.',
    trailerUrl: 'https://www.w3schools.com/html/mov_bbb.mp4',
  },
];

const MovieRecommendation = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showTrailer, setShowTrailer] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % recommendations.length);
    }, 10000); // change every 10 seconds

    return () => clearInterval(interval);
  }, []);

  const movie = recommendations[currentIndex];

  return (
    <div className="recommendation-section">
      <h2>Recommended for You</h2>
      <div className="movie-card">
        <div className="info">
          <h3>{movie.title}</h3>
          <p><strong>Duration:</strong> {movie.duration}</p>
          <p><strong>Episodes:</strong> {movie.episodes}</p>
          <p>{movie.description}</p>

          <div className="buttons">
            <button className="play-btn" onClick={() => setShowTrailer(true)}>
              <PlayArrowIcon style={{ marginRight: '5px' }} />
              Watch Trailer
            </button>
            <button className="fav-btn">
              <FavoriteBorderIcon style={{ marginRight: '5px' }} />
              Favorite
            </button>
            <button className="play-btn">
              <PlayArrowIcon style={{ marginRight: '5px' }} />
              Play Movie
            </button>
          </div>
        </div>
      </div>

      {showTrailer && (
        <div className="trailer-modal">
          <CloseIcon className="close" onClick={() => setShowTrailer(false)} />
          <video controls autoPlay>
            <source src={movie.trailerUrl} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      )}
    </div>
  );
};

export default MovieRecommendation;
